import React,{useState} from 'react'

export const Select = () => {
    const [select,setSelect] = useState("name,function");
    const [from,setFrom] = useState("items");
    const [where,setWhere] = useState("true");
    const [data, setData] = useState([]);
    const onSubmitForm = async(e)=>{
        e.preventDefault();
        try {
            const body =   from + "/" + select + "/" + where;

            const response = await fetch("http://localhost:5321/game/get/" + body);

            const jsonData = await response.json();
            setData({jsonData})
            console.log(data);
        } catch (error) {
            console.log("error")
            console.log(error.message)
        }
    }
    const showResult = () =>{

        if(data.length===0){
            return <p>nothing</p>
        }else{
            const list = data.jsonData;

            return <p>a</p>;
        }
    }
  return (
    <div>
        <form onSubmit={onSubmitForm}>
            <div className='box'>
                <p>Select</p>
                <input type={"text"} value={select} onChange={e=>{setSelect(e.target.value)}}/>
            </div>
            <div className='box'>
                <p>From</p>
                <input type={"text"} value={from} onChange={e=>{setFrom(e.target.value)}}/>
            </div>
            <div className='box'>
                <p>Where</p>
                <input type={"text"} value={where} onChange={e=>{setWhere(e.target.value)}}/>
            </div>
            <button>submit</button>
        </form>
        <div className='table'>
            {showResult(data)}
        </div>
    </div>
  )
}
